#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="C:\Users\User\Desktop\web-astro\node_modules\.pnpm\astro@5.7.3_@types+node@22._4ed15796b3af5ef20baa99417610c787\node_modules\astro\node_modules;C:\Users\User\Desktop\web-astro\node_modules\.pnpm\astro@5.7.3_@types+node@22._4ed15796b3af5ef20baa99417610c787\node_modules;C:\Users\User\Desktop\web-astro\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/c/Users/User/Desktop/web-astro/node_modules/.pnpm/astro@5.7.3_@types+node@22._4ed15796b3af5ef20baa99417610c787/node_modules/astro/node_modules:/mnt/c/Users/User/Desktop/web-astro/node_modules/.pnpm/astro@5.7.3_@types+node@22._4ed15796b3af5ef20baa99417610c787/node_modules:/mnt/c/Users/User/Desktop/web-astro/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../.pnpm/astro@5.7.3_@types+node@22._4ed15796b3af5ef20baa99417610c787/node_modules/astro/astro.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../.pnpm/astro@5.7.3_@types+node@22._4ed15796b3af5ef20baa99417610c787/node_modules/astro/astro.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../.pnpm/astro@5.7.3_@types+node@22._4ed15796b3af5ef20baa99417610c787/node_modules/astro/astro.js" $args
  } else {
    & "node$exe"  "$basedir/../.pnpm/astro@5.7.3_@types+node@22._4ed15796b3af5ef20baa99417610c787/node_modules/astro/astro.js" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
